import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieCard from '../components/MovieCard';
import '../components/Dashboard.css';

const Dashboard = () => {
    const [movies, setMovies] = useState([]);
    const [genres, setGenres] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [query, setQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);

    useEffect(() => {
        const fetchGenres = async () => {
            const cachedGenres = localStorage.getItem('genres');
            if (cachedGenres) {
                setGenres(JSON.parse(cachedGenres));
            } else {
                try {
                    const genreResponse = await axios.get('https://api.themoviedb.org/3/genre/movie/list?api_key=936d7807b77bbb4238f8847775a026ad');
                    const genres = genreResponse.data.genres.reduce((acc, genre) => {
                        acc[genre.id] = genre.name;
                        return acc;
                    }, {});
                    localStorage.setItem('genres', JSON.stringify(genres));
                    setGenres(genres);
                } catch (error) {
                    setError(error.message);
                }
            }
        };

        const fetchMovies = async () => {
            try {
                const movieResponse = await axios.get('https://api.themoviedb.org/3/discover/movie?api_key=19193cff750d7dd975d3bdfcb8c10ae7');
                setMovies(movieResponse.data.results);
                setLoading(false);
            } catch (error) {
                setError(error.message);
                setLoading(false);
            }
        };

        fetchGenres();
        fetchMovies();
    }, []);

    const handleSearch = async (event) => {
        event.preventDefault();
        if (query.trim() === '') return;

        setLoading(true);
        setError(null);

        try {
            const response = await axios.get(`https://api.themoviedb.org/3/search/movie?api_key=19193cff750d7dd975d3bdfcb8c10ae7&query=${query}`);
            setSearchResults(response.data.results);
        } catch (error) {
            setError('Failed to fetch results. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    const topRatedMovies = movies.slice(0, 4);
    const bestOfActionMovies = movies.slice(4, 12);

    return (
        <div className="dashboard">
            <h1>Movie Dashboard</h1>

            <form onSubmit={handleSearch} className="search-form">
                <input
                    type="text"
                    placeholder="Search for a keyword..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    className="search-input"
                />
                <button type="submit" className="search-button">
                    <i className="fas fa-search"></i>
                </button>
            </form>

            {searchResults.length > 0 && (
                <div className="search-results">
                    <h2>Search Results</h2>
                    <div className="movie-list search-list">
                        {searchResults.map(result => (
                            <MovieCard key={result.id} movie={result} genres={genres} />
                        ))}
                    </div>
                </div>
            )}

            <h2>Top Rated</h2>
            <div className="movie-list top-rated">
                {topRatedMovies.map((movie, index) => (
                    <MovieCard key={movie.id} movie={movie} rank={index + 1} category="top-rated" genres={genres} />
                ))}
            </div>

            <h2>Best of Action</h2>
            <div className="movie-list best-of-action">
                {bestOfActionMovies.map((movie, index) => (
                    <MovieCard key={movie.id} movie={movie} rank={index + 1} category="best-of-action" genres={genres} />
                ))}
            </div>
        </div>
    );
}

export default Dashboard;
