import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieCard from '../components/MovieCard';
import '../components/styles.css';

const Dashboard = () => {
    const [movies, setMovies] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        axios.get('https://api.themoviedb.org/3/discover/movie?api_key=19193cff750d7dd975d3bdfcb8c10ae7')
            .then(response => {
                setMovies(response.data.results);
                setLoading(false);
            })
            .catch(error => {
                setError(error.message);
                setLoading(false);
            });
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    const topRatedMovies = movies.slice(0, 4);
    const bestOfActionMovies = movies.slice(4, 12);

    return (
        <div className="dashboard">
            <h1>Movie Dashboard</h1>

            <h2>Top Rated</h2>
            <div className="movie-list top-rated">
                {topRatedMovies.map((movie, index) => (
                    <MovieCard key={movie.id} movie={movie} rank={index + 1} category="top-rated" />
                ))}
            </div>

            <h2>Best of Action</h2>
            <div className="movie-list best-of-action">
                {bestOfActionMovies.map((movie, index) => (
                    <MovieCard key={movie.id} movie={movie} rank={index + 1} category="best-of-action" />
                ))}
            </div>
        </div>
    );
}

export default Dashboard;
