import React from 'react';
import { Link } from 'react-router-dom';
import './styles.css';

const MovieCard = ({ movie, rank, category }) => (
    <div className={`movie-card ${category}`}>
        <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} className="movie-poster" />
        <div className="movie-info">
            <h2>{movie.title}</h2>
            <div className="rating-genre">
                <span className="star">★ {movie.vote_average}</span> | {movie.genre_ids.map(genre => genre).join(' • ')} • Movie
            </div>
            <Link to={`/movie/${movie.id}`} className="details-link">View Details</Link>
        </div>
    </div>
);

export default MovieCard;
