import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Auth.css';
import CryptoJS from 'crypto-js';

const Auth = () => {
    const navigate = useNavigate();
    const [isLogin, setIsLogin] = useState(true);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState(null);

    const handleAuth = (event) => {
        event.preventDefault();
        setError(null);

        if (!email || !password || (!isLogin && password !== confirmPassword)) {
            setError('Please fill in all fields and make sure passwords match.');
            return;
        }

        if (isLogin) {
            const storedUser = JSON.parse(localStorage.getItem('user'));
            if (storedUser && storedUser.email === email) {
                const bytes = CryptoJS.AES.decrypt(storedUser.password, 'secret-key');
                const decryptedPassword = bytes.toString(CryptoJS.enc.Utf8);
                if (decryptedPassword === password) {
                    localStorage.setItem('isAuthenticated', 'true');
                    navigate('/');
                } else {
                    setError('Invalid credentials.');
                }
            } else {
                setError('User not found.');
            }
        } else {
            const encryptedPassword = CryptoJS.AES.encrypt(password, 'secret-key').toString();
            localStorage.setItem('user', JSON.stringify({ email, password: encryptedPassword }));
            localStorage.setItem('isAuthenticated', 'true');
            navigate('/');
        }
    };

    return (
        <div className="auth">
            <form onSubmit={handleAuth} className="auth-form">
                <h2>{isLogin ? 'Login' : 'Register'}</h2>
                {error && <div className="error">{error}</div>}
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className="auth-input"
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="auth-input"
                />
                {!isLogin && (
                    <input
                        type="password"
                        placeholder="Confirm Password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        className="auth-input"
                    />
                )}
                <button type="submit" className="auth-button">
                    {isLogin ? 'Login' : 'Register'}
                </button>
                <div className="toggle-link" onClick={() => setIsLogin(!isLogin)}>
                    {isLogin ? 'Need an account? Register' : 'Already have an account? Login'}
                </div>
            </form>
        </div>
    );
};

export default Auth;
